// CourseCreator AI - Generación Automática de Cursos Educativos
// Creación de contenido educativo con IA para plataformas online

const items = $input.all();
const courseData = items[0].json;

// Configuración de tipos de cursos y estructuras
const courseTypes = {
  'beginner_guide': {
    duration_weeks: 4,
    lessons_per_week: 3,
    difficulty_level: 1,
    engagement_rate: 0.78,
    completion_rate: 0.65,
    price_range: [49, 99]
  },
  'skill_development': {
    duration_weeks: 8,
    lessons_per_week: 4,
    difficulty_level: 2,
    engagement_rate: 0.82,
    completion_rate: 0.72,
    price_range: [99, 299]
  },
  'professional_certification': {
    duration_weeks: 12,
    lessons_per_week: 5,
    difficulty_level: 3,
    engagement_rate: 0.89,
    completion_rate: 0.78,
    price_range: [299, 799]
  },
  'advanced_masterclass': {
    duration_weeks: 16,
    lessons_per_week: 6,
    difficulty_level: 4,
    engagement_rate: 0.91,
    completion_rate: 0.81,
    price_range: [499, 1499]
  }
};

// Analizar topic y determinar tipo de curso óptimo
const topic = courseData.topic || 'general';
const targetAudience = courseData.audience || 'beginners';
const courseType = courseData.course_type || 'skill_development';
const config = courseTypes[courseType] || courseTypes.skill_development;

// Generar estructura completa del curso
const courseStructure = {
  course_title: generateCourseTitle(topic, courseType),
  course_description: generateCourseDescription(topic, targetAudience, config),
  learning_objectives: generateLearningObjectives(topic, config.difficulty_level),
  modules: generateCourseModules(topic, config),
  lessons_per_module: generateLessonsPerModule(config),
  assessments: generateAssessments(config.difficulty_level),
  resources: generateCourseResources(topic),
  certificate_template: generateCertificateInfo(topic, courseType)
};

// Crear contenido con IA
const aiGeneratedContent = {
  module_outlines: generateModuleOutlines(topic, config),
  lesson_scripts: generateLessonScripts(topic, config),
  quiz_questions: generateQuizQuestions(topic, config.difficulty_level),
  assignments: generateAssignments(topic, config.difficulty_level),
  presentation_slides: generateSlideTemplates(topic, config),
  video_scripts: generateVideoScripts(topic, config),
  reading_materials: generateReadingList(topic)
};

// Calcular revenue potencial
const revenueProjection = {
  base_price: Math.floor(Math.random() * (config.price_range[1] - config.price_range[0]) + config.price_range[0]),
  expected_enrollments_monthly: Math.floor(Math.random() * 150) + 50,
  projected_monthly_revenue: 0, // Calculado abajo
  lifetime_value_per_student: calculateLTV(config),
  conversion_rate: config.completion_rate * 0.3,
  churn_rate: 1 - config.completion_rate
};

revenueProjection.projected_monthly_revenue = revenueProjection.base_price * revenueProjection.expected_enrollments_monthly;

// Automatizar creación de contenido
const contentCreation = {
  text_content: {
    modules: generateWrittenContent(topic, config),
    assignments: generateWrittenAssignments(topic),
    assessments: generateWrittenAssessments(topic)
  },
  multimedia_content: {
    video_scripts: generateVideoContent(topic, config),
    presentation_slides: generatePresentationContent(topic),
    infographics: generateVisualContent(topic)
  },
  interactive_content: {
    quizzes: generateInteractiveQuizzes(topic),
    exercises: generatePracticalExercises(topic),
    simulations: generateSimulations(topic)
  }
};

// Integración con plataformas educativas
const platformIntegrations = {
  'teachable': {
    course_upload: true,
    student_enrollment: true,
    payment_processing: true,
    completion_tracking: true
  },
  'udemy': {
    course_listing: true,
    coupon_management: true,
    review_monitoring: true,
    promotion_tools: true
  },
  'coursera': {
    course_partnership: false, // Requires approval
    credential_issuing: true,
    peer_assessment: true
  },
  'custom_lms': {
    api_integration: true,
    scorm_compliance: true,
    analytics_tracking: true,
    certificate_generation: true
  }
};

return {
  json: {
    action: 'course_creation_initiated',
    course_id: courseData.course_id,
    course_type: courseType,
    topic: topic,
    target_audience: targetAudience,
    course_structure: courseStructure,
    ai_generated_content: aiGeneratedContent,
    revenue_projection: revenueProjection,
    content_creation: contentCreation,
    platform_integrations: platformIntegrations,
    estimated_monthly_revenue: Math.floor(revenueProjection.projected_monthly_revenue),
    campaign_id: 'coursecreator_' + Date.now()
  }
};

// Funciones auxiliares
function generateCourseTitle(topic, courseType) {
  const prefixes = {
    'beginner_guide': 'Guía Completa de',
    'skill_development': 'Desarrollo Profesional en',
    'professional_certification': 'Certificación Profesional en',
    'advanced_masterclass': 'Masterclass Avanzada de'
  };

  return `${prefixes[courseType]} ${topic}`;
}

function generateCourseDescription(topic, audience, config) {
  return `Domina ${topic} desde cero hasta nivel profesional. Este curso de ${config.duration_weeks} semanas está diseñado para ${audience} y incluye ${config.lessons_per_week * config.duration_weeks} lecciones prácticas con proyectos reales.`;
}

function generateLearningObjectives(topic, difficulty) {
  return [
    `Comprender los fundamentos de ${topic}`,
    `Aplicar conocimientos en proyectos prácticos`,
    `Desarrollar habilidades específicas de ${topic}`,
    `Crear un portfolio profesional`,
    `Obtener certificación reconocida en la industria`
  ].slice(0, Math.min(5, difficulty + 3));
}

function generateCourseModules(topic, config) {
  const moduleCount = Math.ceil(config.duration_weeks / 2);
  return Array.from({length: moduleCount}, (_, i) => ({
    module_number: i + 1,
    title: `Módulo ${i + 1}: ${generateModuleTitle(topic, i)}`,
    description: `Conceptos y prácticas de ${topic} para el módulo ${i + 1}`,
    duration_hours: Math.floor(config.duration_weeks * 2 / moduleCount),
    lessons: config.lessons_per_week * 2
  }));
}

function generateModuleTitle(topic, moduleIndex) {
  const titles = {
    0: 'Fundamentos y Conceptos Básicos',
    1: 'Aplicaciones Prácticas',
    2: 'Técnicas Avanzadas',
    3: 'Proyectos Reales',
    4: 'Optimización y Mejores Prácticas'
  };

  return titles[moduleIndex] || `Profundización en ${topic}`;
}

function generateLessonsPerModule(config) {
  return Math.floor(config.lessons_per_week * config.duration_weeks / 4); // Aproximadamente 2 por semana
}

function generateAssessments(difficulty) {
  return [
    {
      type: 'quiz',
      frequency: 'weekly',
      questions: 10 + difficulty * 5
    },
    {
      type: 'assignment',
      frequency: 'bi_weekly',
      projects: 2 + difficulty
    },
    {
      type: 'final_exam',
      frequency: 'end_of_course',
      comprehensive: true
    }
  ];
}

function generateCourseResources(topic) {
  return {
    textbooks: [`Manual Completo de ${topic}`, `${topic} Práctico`],
    templates: [`Template de Proyecto ${topic}`, `Checklist ${topic}`],
    tools: [`Herramientas de ${topic}`, `Software Recomendado`],
    community: [`Grupo de Discusión ${topic}`, `Discord de Estudiantes`] 
  };
}

function generateCertificateInfo(topic, courseType) {
  return {
    title: `Certificado en ${topic}`,
    issued_by: 'CourseCreator AI Academy',
    validity: 'Lifetime',
    verifiable: true
  };
}

function generateModuleOutlines(topic, config) {
  return Array.from({length: 4}, (_, i) => ({
    module: i + 1,
    title: generateModuleTitle(topic, i),
    outline: `Tema detallado del módulo ${i + 1} de ${topic}`,
    key_points: [`Punto clave 1 de ${topic}`, `Punto clave 2 de ${topic}`, `Punto clave 3 de ${topic}`],
    practical_exercises: `Ejercicios prácticos de ${topic} para el módulo ${i + 1}`
  }));
}

function generateLessonScripts(topic, config) {
  const totalLessons = config.lessons_per_week * config.duration_weeks;
  return Array.from({length: totalLessons}, (_, i) => ({
    lesson_number: i + 1,
    title: `Lección ${i + 1}: ${topic} en Acción`,
    duration_minutes: 45 + Math.floor(Math.random() * 30),
    script: `Script detallado para enseñar ${topic} en la lección ${i + 1}`,
    key_concepts: [`Concepto 1 de ${topic}`, `Concepto 2 de ${topic}`],
    activities: [`Actividad práctica ${i + 1}`, `Ejercicio interactivo ${i + 1}`]
  }));
}

function generateQuizQuestions(topic, difficulty) {
  return Array.from({length: 50 + difficulty * 20}, (_, i) => ({
    question_id: i + 1,
    question: `Pregunta sobre ${topic} - Nivel ${difficulty}`,
    type: Math.random() > 0.7 ? 'multiple_choice' : 'true_false',
    options: [
      `Opción A sobre ${topic}`,
      `Opción B sobre ${topic}`,
      `Opción C sobre ${topic}`,
      `Opción D sobre ${topic}`
    ],
    correct_answer: Math.floor(Math.random() * 4),
    explanation: `Explicación detallada sobre ${topic}`
  }));
}

function generateAssignments(topic, difficulty) {
  return Array.from({length: difficulty * 2}, (_, i) => ({
    assignment_id: i + 1,
    title: `Proyecto ${i + 1}: Aplicación de ${topic}`,
    description: `Proyecto práctico para aplicar conocimientos de ${topic}`,
    requirements: [`Requisito 1 para ${topic}`, `Requisito 2 para ${topic}`, `Requisito 3 para ${topic}`],
    submission_format: 'PDF + Código si aplica',
    evaluation_criteria: [`Criterio 1`, `Criterio 2`, `Criterio 3`]
  }));
}

function generateSlideTemplates(topic, config) {
  const totalSlides = config.duration_weeks * 20; // 20 slides por semana
  return {
    total_slides: totalSlides,
    templates: {
      title_slide: `Plantilla título para ${topic}`,
      content_slide: `Plantilla contenido para ${topic}`,
      quiz_slide: `Plantilla preguntas para ${topic}`,
      summary_slide: `Plantilla resumen para ${topic}`
    },
    themes: ['Professional Blue', 'Modern Green', 'Corporate Gray', 'Creative Orange']
  };
}

function generateVideoScripts(topic, config) {
  const totalVideos = config.duration_weeks * 2;
  return Array.from({length: totalVideos}, (_, i) => ({
    video_number: i + 1,
    title: `Video ${i + 1}: ${topic} Demostrado`,
    duration_minutes: 15 + Math.floor(Math.random() * 20),
    script: `Script completo para video demostrativo de ${topic}`,
    scenes: [
      `Escena introducción ${topic}`,
      `Escena demostración ${topic}`,
      `Escena práctica ${topic}`,
      `Escena resumen ${topic}`
    ],
    visual_aids: [`Diagrama de ${topic}`, `Código ejemplo ${topic}`, `Screenshot ${topic}`]
  }));
}

function generateReadingList(topic) {
  return [
    {
      title: `Libro Fundamentos de ${topic}`,
      author: `Autor Experto en ${topic}`,
      pages: 300,
      priority: 'high'
    },
    {
      title: `Guía Práctica de ${topic}`,
      author: `Especialista en ${topic}`,
      pages: 250,
      priority: 'medium'
    },
    {
      title: `${topic} Avanzado: Casos de Estudio`,
      author: `Investigador de ${topic}`,
      pages: 180,
      priority: 'low'
    }
  ];
}

function generateWrittenContent(topic, config) {
  const totalModules = 4;
  return Array.from({length: totalModules}, (_, i) => ({
    module: i + 1,
    content_type: 'written',
    word_count: 5000 + i * 1000,
    topics: [`Conceptos básicos de ${topic}`, `Aplicaciones prácticas ${topic}`, `Casos de estudio ${topic}`]
  }));
}

function generateWrittenAssignments(topic) {
  return Array.from({length: 6}, (_, i) => ({
    assignment: i + 1,
    type: 'written',
    word_count: 1000 + Math.floor(Math.random() * 1500),
    topic: `${topic} - Proyecto ${i + 1}`,
    format: 'Essay/Report'
  }));
}

function generateWrittenAssessments(topic) {
  return Array.from({length: 8}, (_, i) => ({
    assessment: i + 1,
    type: 'written_exam',
    questions: 15 + Math.floor(Math.random() * 10),
    time_limit_minutes: 60 + Math.floor(Math.random() * 30),
    topic: `${topic} - Evaluación ${i + 1}`
  }));
}

function generateVideoContent(topic, config) {
  return {
    total_videos: config.duration_weeks * 2,
    total_duration_hours: config.duration_weeks * 3,
    video_types: ['Lecture', 'Demo', 'Tutorial', 'Case Study'],
    quality_standards: ['1080p minimum', 'Audio clear', 'Subtitles included']
  };
}

function generatePresentationContent(topic) {
  return {
    total_presentations: 20,
    slides_per_presentation: 25 + Math.floor(Math.random() * 15),
    design_templates: 5,
    animation_style: 'Professional'
  };
}

function generateVisualContent(topic) {
  return {
    infographics: 15,
    diagrams: 10,
    flowcharts: 5,
    mind_maps: 3,
    tools_used: ['Canva', 'Miro', 'Figma']
  };
}

function generateInteractiveQuizzes(topic) {
  return Array.from({length: 20}, (_, i) => ({
    quiz_id: i + 1,
    title: `Quiz ${i + 1}: ${topic}`,
    questions: 10 + Math.floor(Math.random() * 10),
    time_limit: 15 + Math.floor(Math.random() * 15),
    attempts_allowed: 3,
    randomize_questions: true
  }));
}

function generatePracticalExercises(topic) {
  return Array.from({length: 15}, (_, i) => ({
    exercise_id: i + 1,
    title: `Ejercicio ${i + 1}: ${topic} en la Práctica`,
    difficulty: ['Fácil', 'Medio', 'Difícil'][Math.floor(Math.random() * 3)],
    estimated_time: 30 + Math.floor(Math.random() * 90),
    tools_needed: [`Herramienta 1 ${topic}`, `Herramienta 2 ${topic}`]
  }));
}

function generateSimulations(topic) {
  return Array.from({length: 5}, (_, i) => ({
    simulation_id: i + 1,
    title: `Simulación ${i + 1}: ${topic} Real`,
    scenario: `Escenario realista de ${topic} para practicar`,
    interactive_elements: [`Elemento interactivo 1`, `Elemento interactivo 2`],
    learning_objective: `Objetivo específico de aprendizaje ${topic}`
  }));
}

function calculateLTV(config) {
  return config.price_range[1] * config.completion_rate * 1.2; // Base price * completion rate * upsell factor
}
